mapper.select:
SELECT *
  FROM   customer 
 WHERE   custom_id = #{col1}
   AND   custom_pass = #{col2}

SELECT COUNT(*)
  FROM customer 
 WHERE custom_id = #{col1}
   AND custom_pass = #{col2}

SELECT COUNT(*)
  FROM address 
 WHERE custom_id = #{col1}

SELECT count(1)
  FROM basket 
 WHERE custom_id = #{col1}
   AND item_seq  = #{col2}

